.modal{
    z-index: 10000000;
    background-color: #44444481;
 }
 
 .borderless td, .borderless th {
     border: none;
 }
 
 
   
   #modal_peminjaman .modal-dialog.full-screen {
     position:fixed;           
     left:0px;
     right:0px;
     top:0px;
     bottom:0px;
   }
   
   #modal_peminjaman .modal-dialog.full-screen .modal-content {
     position:absolute;
     left:10px;
     right:10px;
     top:10px;
     bottom:10px;
   }
  
   #modal_peminjaman .modal-dialog.full-screen .modal-content .modal-body {
     overflow-y: auto;
     position: absolute;
     top: 0;
     bottom: 0;
     left:0;
     right:0;
     margin-top: 55px; 
     margin-bottom: 80px;  
   }
   
   #modal_peminjaman .modal-dialog.full-screen .modal-content .modal-footer {
     position:absolute;
     bottom:0;
     left:0;
     right:0;
   }

 
 
   #export{
       background-color: #fff;
   }
 
  #modal_peminjaman .main-footer {
     position: fixed;
     left: 0;
     bottom: 0;
     width: 100%;
     background-color: red;
     color: white;
     text-align: center;
  }
 
  #notif_paragraf{
   width: 300px;
   white-space: nowrap;
   overflow: hidden;
   text-overflow: ellipsis;
  }
 
  #badge {
   display: inline-block;
   border: 1px solid transparent;
   color: #000;
   border-color: #2196F3;
 }

